  from record_cover_tb
 where RECORD_NO = #{no}

mapper.update:
update record_tb
   set picture = 1
 where RECORD_NO = #{no}

mapper.select:
		SELECT *, COALESCE((SELECT SUM(TRAVEL_PRICE) FROM RECORD_TRAVELPRICE_TB RT WHERE RT.RECORD_NO = R.RECORD_NO AND RECORD_NO = #{no}),0) AS TOTAL_PRICE
  		  FROM RECORD_TB R
         WHERE R.RECORD_NO = #{no};

