mapper.select:
select *
  from record_location_tb
 where RECORD_NO = #{no}

select count(CONTENT_CODE)
  from record_location_liked_tb
 where CONTENT_CODE = #{code}

select CONTENT_CODE as contentId, USER_UID as uid
  from record_location_liked_tb
 where CONTENT_CODE = #{contentId}
   and USER_UID = #{uid}

mapper.delete:
